import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllCourses } from "../services/CourseServices";



export default function Home() {
    const [courseData,setCourseData]=useState([]);

  useEffect(()=>{
  getAllCourses()
    .then(res=>{
      console.log(res.data);
      setCourseData(res.data);
    }).catch(err=> console.log(err));
  },[]);




  return (
    <div className="card m-2">
      <div className="card-body">
      <div className="card-header text-white"style={{backgroundColor: "#003366"}}>
             <h5 className="card-title text-center">Course List</h5>
             </div>
      <h2 className=" m-5" style={{backgroundColor:"red"}}> </h2>
      <div className="row container m-3" >
   {courseData.map(course=>


       <div className="col-md-4 m-2" key={course.id} >
         <div className="card" style={{ width: "18rem" }}>
           <div className="card-body">
           <div className="card-header text-white"style={{backgroundColor: "#003366"}}>
             <h5 className="card-title">{course.name}</h5>
             </div>
             <p className="card-text p-2">{course.description}</p>
             <p className="card-text"> Duration : {course.duration} months</p>

             <Link to={`/courseEnquiry/${course.id}`} className="btn btn-warning">Enquire Now</Link>
             {/* <Link to={`/courseEnquiry/${props.id}`} className="btn btn-info">More</Link> */}

           </div>
         </div>
       </div>




       )}
       </div>
</div>
      </div>

  );
}
