  const [enquirydata, setEnquiryData] = useState([]);
  const [courseData, setCourseData] = useState([]);

  useEffect(() => {
    allEnquiries()
      .then((res) => {
        console.log(res.data);
        setEnquiryData(res.data);
      })
      .catch((err) => console.log(err));
  }, []);




